import React, {useState, useEffect} from 'react'
import './ListItemContainer.css'
import ItemList from '../ItemList/ItemList'
import AddToCart from '../AddToCart/AddToCart'

/*export default function ListItemContainer(){

  let listItems = [{name: 'CardItem1'}, {name: 'CardItem2'}, {name: 'CardItem3'}]
    return(
        <div id = 'listContainer'>
          <h1>Listado de items del E-Commerce</h1>
          {listItems.map(item => {
            return(
              <div style = {{margin: '10% 0 10% 0'}}>{item.name} <div><AddToCart /></div></div>
              )
          })}
        </div>
    )
}
 */
/* function getItems(){
  return new Promise((res, rej) => {
    res([{name: 'CardItem1'}, {name: 'CardItem2'}, {name: 'CardItem3'}])
  })
} 
// Esto es igual a la const de abajo!
*/

const getItems = () => {
  return new Promise((res, rej) => {
    setTimeout(() => {
      /* res([{name: 'CardItem1'}, {name: 'CardItem2'}, {name: 'CardItem3'}]) */
    }, 3000)
    rej('No hay cursos disponibles!')
  })
}

export default function ListItemContainer(){
  const[item, setItem] = useState([])
  const[error, setError] = useState(null)
  useEffect(() => {
    getItems().then(items => {
      setItem(items)
    },
    err => {
      setError(err)
    })
  }, [])
  return <ItemList items = {item}/>
}
