import React from 'react'
import {useState} from 'react'
import {Button} from '@material-ui/core'

export default function AddToCart(){
    const [state, setState] = useState(0)
    function addState(){
        setState(state + 1)
    }
    function restState(){
        if(state < 1){
            setState(state)
        }
        else{
            setState(state - 1)
        }
    }
    return(
        <div>
            <Button onClick = {restState} style = {{color: 'hotpink', fontSize: '2rem'}}>-</Button><span>{state}</span><Button onClick = {addState} style = {{color: 'hotpink', fontSize: '2rem'}}>+</Button>
        </div>
    )
}
